import React from 'react'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";


export default function EventSection() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    pauseOnHover: true
  };

  return (
    <div className='event-section-container'>
      <div className="event-title-container">
        <h2>Most Popular Events</h2>
        <p>Our key events about the Opera Houses's 50 years of History</p>
      </div>
      <Slider {...settings}>
        <div>
          <img src="https://images.adsttc.com/media/images/5344/7719/c07a/8091/a000/02d7/newsletter/JornUtzon.jpg?1396995856" alt="image 1"/>
        </div>
        <div>
          <img src="https://images.adsttc.com/media/images/5344/7719/c07a/8091/a000/02d7/newsletter/JornUtzon.jpg?1396995856" alt="image 2"/>
        </div>
        <div>
          <img src="path/to/image3.jpg" alt="image 3"/>
        </div>
        {/* add more slides as needed */}
      </Slider>
    </div>
  )
}
